function parseAction(actionId) {
    if (actionId === undefined) {
        return "";
    }
    switch (actionId) {
        case -2:
            return "Offline";
        case -1:
            return "Idle";
        case 255:
            return "Idle";
        case 0:
            return "Printing";
        case 1:
            return "Auto bed leveling";
        case 2:
            return "Heatbed preheating";
        case 3:
            return "Sweeping XY mech mode";
        case 4:
            return "Changing filament";
        case 5:
            return "M400 pause";
        case 6:
            return "Paused due to filament runout";
        case 7:
            return "Heating hotend";
        case 8:
            return "Calibrating extrusion";
        case 9:
            return "Scanning bed surface";
        case 10:
            return "Inspecting first layer";
        case 11:
            return "Identifying build plate type";
        case 12:
            return "Calibrating Micro Lidar";
        case 13:
            return "Homing toolhead";
        case 14:
            return "Cleaning nozzle tip";
        case 15:
            return "Checking extruder temperature";
        case 16:
            return "Printing was paused by the user";
        case 17:
            return "Pause of front cover falling";
        case 18:
            return "Calibrating the micro lidar";
        case 19:
            return "Calibrating extrusion flow";
        case 20:
            return "Paused due to nozzle temperature malfunction";
        case 21:
            return "Paused due to heat bed temperature malfunction";
        case 22:
            return "Filament Unloading";
        case 23:
            return "Paused due to skipped step";
        case 24:
            return "Filament Loading";
        case 25: 
            return "Motor Noise Calibration";
        case 26:
            return "Paused due to AMS lost";
        case 27:
            return "Paused due to low speed of the heat break fan";
        case 28:
            return "Paused due to chamber temperature control error";
        case 29:
            return "Cooling chamber";
        case 30:
            return "Paused by the Gcode inserted by user"
        case 31:
            return "Motor noise showoff"
        case 32:
            return "Nozzle filament covered detected pause"
        case 33:
            return "Cutter error pause"
        case 34:
            return "First layer error pause"
        case 35:
            return "Nozzle clog pause"
        default:
            return actionId.toString()
    }
}
let stages = [];
for (var x = -2; x <= 31; x +=1) {
    stages.push(parseAction(x));
}
flow.set("stageParser", parseAction);
flow.set("stages", stages);

let statuses = ["OFFLINE", "IDLE", "PREPARE", "RUNNING", "PAUSE", "FINISH", "FAILED"];
flow.set("statuses", statuses);


function parseAMSStatus(status) {
    let sub_status = status & 0xFF;
    let main_status = (status & 0xFF00) >> 8;
    switch (main_status) {
        case 0:
        case 0x00:
            return "AMS Idle";
        case 1:
        case 0x01:
            return "AMS Filament Change";
        case 2:
        case 0x02:
            return "AMS RFID Identifying";
        case 3:
        case 0x03:
            return "AMS Assist";
        case 4:
        case 0x04:
            return "AMS Calibration";
        case 16:
        case 0x10:
            return "AMS Self-Check";
        case 32:
        case 0x20:
            return "AMS Debug";
        case 255:
        case 0xFF:
        default:
            return "Unknown";
    }
}
let ams_statuses = ["AMS Idle", "AMS Filament Change", "AMS RFID Identifying", "AMS Assist", "AMS Calibration", "AMS Self-Check", "AMS Debug", "Unknown"];
flow.set("amsStatusParser", parseAMSStatus);

function parseRFIDStatus(status) {
    //todo: make into list method for ha
    switch (status) {
        case 0:
            return "RFID Idle";
        case 1:
            return "RFID Reading";
        case 2:
            return "GCode Translating";
        case 3:
            return "GCode Running";
        case 4:
            return "RFID Assistant";
        case 5:
            return "Switch Filament";
        case 6:
            return "Has Filament";
        default:
            return "Unknown";
    }
} 
flow.set("RFIDStatusParser", parseRFIDStatus);

function parseFWRFlag(flag2, key) {
    if (key == "is_FW_R") {
        return (flag2 & 0x1) != 0;
    }
    else if (key == "is_FW_signed") {
        let val = ((flag2 >> 1) & 0x1) // and >> 2?
        if (val == 0) {
            return "unknown";
        }
        else if (val == 1) {
            return "signed";
        }
        else if (val == 2){
            return "unsigned";
        }
        return "unknown";
    }
    else if (key == "is_FW_unlocked") {
        return ((flag2 >> 3) & 0x1) != 0;
    }
    else if (key == "is_SSH_started") {
        return ((flag2 >> 4) & 0x1) != 0;
    }
    return undefined;
}
