        } // HT ? 
        else {
            switch(m.name) {
                case "ota":
                    data.printer.SW_version = m.sw_ver;
                    break;
                case "rv1126":
                    data.printer.serial_number = m.sn;
                    data.printer.HW_version = m.hw_ver;
                    break;
                case "esp32":
                    data.printer.serial_number = m.sn;
