function getFriendlyName(str) {
    var i, word = str.split('_');
    for (i = 0; i < word.length; i++) {
        word[i] = word[i].charAt(0).toUpperCase() + word[i].slice(1);
    }
    let final_word = word.join(' ');
    final_word = final_word.replace("Ams", "AMS");
    final_word = final_word.replace("Rfid", "RFID")
    final_word = final_word.replace("Mqtt", "MQTT");
    final_word = final_word.replace("Mc", "MC");
    final_word = final_word.replace("X1p", "X1P")
    return final_word;
}
