                msg.payload.print.nodered_advanced_flow_status = "Up to Date";
            }
            else {
                msg.payload.print.nodered_advanced_flow_status = "Update Available";
            }
        }
    }
    if (flow.get("has_x1p_flow")) {
        if (flow.get("x1plus_flow_version") != undefined) {
            msg.payload.print.nodered_x1p_flow_version = flow.get("x1plus_flow_version");
        }

        if (flow.get("x1p_is_latest_flow") != undefined) {
            if (flow.get("x1p_is_latest_flow")) {
                msg.payload.print.nodered_x1p_flow_status = "Up to Date";
            }
            else {
                msg.payload.print.nodered_x1p_flow_status = "Update Available";
            }
        }
    }

    if (!model.startsWith("A1") && !model.startsWith("H2")) {
        if (can_request) {
            msg.payload.print.logo_light_on = "";
            msg.payload.print.logo_light_off = "";
            msg.payload.print.nozzle_light_on = "";
            msg.payload.print.nozzle_light_off = "";
        }
    }
    if (flow.get("has_db_flow") != undefined && flow.get("has_db_flow") == true) {
        msg.payload.print.active_print_time = "";
        msg.payload.print.filament_printed = "";
    }

    if (can_request) 
        msg.payload.print.home = "";

    msg.payload.print.mqtt_connection_type = flow.get("P1P_CLOUD_MODE") ? "Bambu Cloud" : "Local";
    if (flow.get("has_ipcam") && can_request) {
        msg.payload.print.toggle_recording = "";
        //msg.payload.print.toggle_timelapse = "";////////////////////////
    }

    if (can_request) {
        if(flow.get("ams_count") > 0) {
            msg.payload.print.toggle_auto_switch = "";
        }
        if (flow.get("model").startsWith("X1")) {
            msg.payload.print.toggle_first_layer_inspection = "";
            msg.payload.print.toggle_buildplate_detection = "";
        }
        msg.payload.print.set_bed_temp = "";
        msg.payload.print.set_nozzle_temp = "";
        msg.payload.print.set_speed_percent = "";
        msg.payload.print.toggle_auto_recovery = "";
        let can_heat_chamber = (flow.get("model") == "X1E"); // change if retroactive
        // H2 isn't controllable
        if(can_heat_chamber) {
            msg.payload.print.set_chamber_temp = "";
        }
    }

    //msg.payload.print.print_preview = "";

    if (flow.get("has_adv_flow")) {
        //msg.payload.print.plate_type = "";
        msg.payload.print.print_preview = "";
        msg.payload.print.force_ftp_fetch = "";
    }

    if (can_request){
        msg.payload.print.clear_external_spool = "";
    }
    if(model == "X"+"1C" || model == "X"+"1") {
        let versions = flow.get("mc_print_versions");
        if (versions[model] != undefined) {
            let valid_versions = versions[model];
            if (valid_versions.length > 0 && valid_versions.includes(flow.get("fw_version"))) {
                msg.payload.print.last_calibrated_pa = "";
            }
        }

      /*  if (msg.payload.printer != undefined && msg.payload.printer.SW_version != undefined && msg.payload.printer.SW_version != ""
        && flow.get("x1plus_installed") != undefined && flow.get("x1plus_installed") == true) {
            if(msg.payload.printer.SW_version.startsWith("99")) {
                if(flow.get("x1plus_active") == undefined || flow.get("x1plus_active") == false) {
                    node.warn("[INFO] X1Plus CFW is currently Active");
                }
                flow.set("x1plus_active", true); 
            }
            else{
                if(flow.get("x1plus_active") == undefined || flow.get("x1plus_active") == true){
                    node.warn("[INFO] X1Plus CFW is currently Not Active");
                }
                flow.set("x1plus_active", false); 
            }
        }*/
        if (flow.get("x1plus_installed") != undefined && flow.get("x1plus_installed") == true) {
            if (flow.get("x1plus_active") != undefined) {
                if (flow.get("x1plus_active")) 
                    msg.payload.print.x1plus = "Active";
                else
                    msg.payload.print.x1plus = "Inactive";
            }
        }
    }

    if (can_request)
        msg.payload.print.unload_filament = "";

    if(snapshot_url != undefined && snapshot_url != "" && snapshot_url != "{" + "SNAPSHOT_URL" + "}") {
        msg.payload.print.snapshot_url = snapshot_url;
    }

    if (camera_stream_url != undefined && camera_stream_url != "" && camera_stream_url != "{" + "STREAM_URL" + "}") {
        msg.payload.print.camera_stream_url = camera_stream_url;
    }

    if(msg.payload.print.ipcam != undefined && msg.payload.print.ipcam.rtsp_url != undefined) {
        if(msg.payload.print.ipcam.rtsp_url == "disable") {
            msg.payload.print.camera_rtsp = "Disabled";
        }
        else if (msg.payload.print.ipcam.rtsp_url.startsWith("rtsp")) {
            msg.payload.print.camera_rtsp = msg.payload.print.ipcam.rtsp_url;
            if(msg.payload.print.ipcam.ipcam_dev == undefined) {
                delete msg.payload.print.ipcam;
            }
        }
        // rtsps://bblp:[access_code]@[lan_ip]:322/streaming/live/1    TCP
    }

    if(msg.payload.print.cali_version != undefined && msg.payload.print.cali_version >= 0 && model.includes("X1")) {
        flow.set("supports_calibration", true);
    }

    node.status({ fill: "green", shape: "ring", text: "Data Received" });

    if (flow.get("wws_bambu_" + flow.get("printer_serial")) != undefined) {
        let d = flow.get("wws_bambu_" + flow.get("printer_serial"));
        msg.payload.print["IP_Address"] = d["IP Address"];
        msg.payload.print["Device_Dev_Name"] = d["Name"];
        msg.payload.print["Connection_Type"] = d["Connection Type"];
        msg.payload.print["Series"] = d["Series"];
        msg.payload.print["SSDP_Model"] = d["Model"];
    }

}


if (msg.payload.info != undefined || msg.payload.security != undefined || msg.payload.system != undefined) {
    msg.printer_name = machine_name;
    msg.machine_serial = machine_serial;
    msg.manufacturer = manufacturer;
    msg.model = model;

    node.status({ fill: "green", shape: "ring", text: "Data Received" });
}
if (msg.payload == undefined ||
    (Object.keys(msg.payload) != undefined
        && Object.keys(msg.payload).length == 0)) { return };
node.send([msg, null]);
