var filament_library = flow.get("bambu_filament_library");
if(filament_library != undefined) {
    if (msg.payload.print.ams !== undefined && msg.payload.print.ams.ams != undefined) {
        for (var ams of msg.payload.print.ams.ams) {
            for (var tray of ams.tray) {
                if (tray.tray_info_idx !== undefined && tray.tray_info_idx !== "") {
                    if(filament_library.hasOwnProperty(tray.tray_info_idx)) {
                        var match = filament_library[tray.tray_info_idx];
                        tray.tray_type = match.tray_type;
                        tray.tray_sub_brands = match.tray_sub_brands;
                        if (tray.tray_diameter == "0.00") {
                            tray.tray_diameter = "1.75";
                        }
                    }
                }
            }
        }
    }
    if(msg.payload.print.vt_tray != undefined) {
        if (msg.payload.print.vt_tray.tray_info_idx != undefined) {
            if (filament_library.hasOwnProperty(msg.payload.print.vt_tray.tray_info_idx)) {
                var match = filament_library[msg.payload.print.vt_tray.tray_info_idx];
                msg.payload.print.vt_tray.tray_type = match.tray_type;
                msg.payload.print.vt_tray.tray_sub_brands = match.tray_sub_brands;
                if (msg.payload.print.vt_tray.tray_diameter == "0.00") {
                    msg.payload.print.vt_tray.tray_diameter = "1.75";
                }
                if(msg.payload.print.vt_tray.tray_type == undefined) {
                    msg.payload.print.vt_tray.tray_type = "Empty";
                }
            }
        }
    }

    node.send(msg);
}

function parsePercent(speed) {
    let percent = (parseInt(speed) / 15) * 100;
    return Math.ceil(percent / 10) * 10;
}

// Overwrite
if(msg.payload.print.big_fan1_speed != undefined) {
    msg.payload.print.big_fan1_speed = parsePercent(msg.payload.print.big_fan1_speed);
}
if (msg.payload.print.big_fan2_speed != undefined) {
    msg.payload.print.big_fan2_speed = parsePercent(msg.payload.print.big_fan2_speed);
}

if (msg.payload.print.heatbreak_fan_speed != undefined) {
    msg.payload.print.heatbreak_fan_speed = parsePercent(msg.payload.print.heatbreak_fan_speed);
}

if (msg.payload.print.cooling_fan_speed != undefined) {
    msg.payload.print.cooling_fan_speed = parsePercent(msg.payload.print.cooling_fan_speed);
}

node.send(msg)

let ams_in_use = false;
let virtual_in_use = false;
let used_id = undefined;
if (msg.payload.print.ams != undefined && msg.payload.print.ams.tray_now !== undefined) {
    used_id = parseInt(msg.payload.print.ams.tray_now);
    flow.set("used_tray_id", used_id);
    if (used_id == 255 || used_id < 0) {
        flow.set("current_filament", "");
    }
    else if (used_id == 254) {
        ams_in_use = false;
        virtual_in_use = true;
    }
    if (msg.payload.print.ams.ams != undefined)
    {
        for (var ams of msg.payload.print.ams.ams) {
            for (var tray of ams.tray) {
                if (tray.tray_type == undefined || tray.tray_type == "") {
                    tray.tray_type = "Empty";
                    tray.tray_color = "#808080";
                } 
                else {
                    tray.tray_color = "#" + tray.tray_color;
                }

                tray.in_use = false;

                var id = parseInt(tray.id);

                if (used_id == (id + 4* parseInt(ams.id))) {
                    tray.in_use = true;
                    ams_in_use = true;
                    virtual_in_use = false;
                    if(tray.tray_type != "Empty") {
                        //msg.payload.print.filament = tray.tray_type;
                        flow.set("current_filament", tray.tray_type);
                    }
                    else {
                        flow.set("current_filament", "");
                    }
                }

                tray.ams_id = ams.id;
            }
        }
    }
}
if(flow.get("used_tray_id") == 254) {
    virtual_in_use = true;
    ams_in_use = false;
}
if(msg.payload.print.vt_tray != undefined) {
    //msg.payload.print.filament = "Unknown";
    if(msg.payload.print.vt_tray.tray_type != undefined) {
        msg.payload.print.vt_tray.type = msg.payload.print.vt_tray.tray_type;
        delete msg.payload.print.vt_tray.tray_type;
        msg.payload.print.filament = msg.payload.print.vt_tray.type;
        if (msg.payload.print.vt_tray.tray_color != undefined) {
            msg.payload.print.vt_tray.color = msg.payload.print.vt_tray.tray_color;
            delete msg.payload.print.vt_tray.tray_color;
        }
        if (msg.payload.print.vt_tray.color == undefined || msg.payload.print.vt_tray.color == "") {
            msg.payload.print.vt_tray.color  = "#808080";
        }
        if (msg.payload.print.vt_tray.type == undefined || msg.payload.print.vt_tray.type == "") {
            msg.payload.print.vt_tray.type = "Empty";
            msg.payload.print.vt_tray.color = "#808080";
        }

        //msg.payload.print.filament = msg.payload.print.vt_tray.type;
        //if(msg.payload.print.filament == "Empty") {
            //msg.payload.print.filament = "Unknown";
        //}

        if (msg.payload.print.vt_tray.color != undefined && !msg.payload.print.vt_tray.color.startsWith("#")) {
            msg.payload.print.vt_tray.color = "#" + msg.payload.print.vt_tray.color;
        }
    }
    if(!ams_in_use && virtual_in_use) {
        msg.payload.print.vt_tray.in_use = true;
        if (msg.payload.print.vt_tray.type != undefined && msg.payload.print.vt_tray.type != "Empty") { 
            flow.set("current_filament", msg.payload.print.vt_tray.type );
        }
        else if (msg.payload.print.vt_tray.type != undefined ) {
            flow.set("current_filament", "");
        }
    }

}

