let longcode_parser = flow.get("hms_longcode_parser");

let hms_data = {
    "HMS_Info": [],
    "HMS_Common": [],
    "HMS_Serious": [],
    "HMS_Fatal": [],
    "HMS_Unknown": []
};


let template = []
let count = 0;
let hms_data_dictionary = undefined;
if(msg.payload.print.hms != undefined){
    for (var hms_code of msg.payload.print.hms) {
        if (hms_code.attr == undefined || hms_code.code == undefined || hms_code.attr == "" || hms_code.code == "") {
            continue;
        }
        var longcode = longcode_parser(hms_code.attr, hms_code.code);
        /*var attr = "";
        if(hms_code.attr != undefined && hms_code.attr != "") {
            attr=DecimalHexTwosComplement(hms_code.attr);
        }
        var code = "";
        if(hms_code.code != undefined && hms_code.code != "") {
            code = DecimalHexTwosComplement(hms_code.code);
        }*/

        let full_code = longcode.replace(/(.{4})/g, "$1_");
        full_code = full_code.substring(0, full_code.length - 1);
        let url = "https://wiki.bambulab.com/en/x1/troubleshooting/hmscode/"+full_code;

        let description = "";
        if (flow.get("hms_data") != undefined && hms_data_dictionary == undefined) {
            hms_data_dictionary = flow.get("hms_data");
        };

        if (hms_data_dictionary.hms[full_code.toLowerCase().replaceAll("_", "")] != undefined) {
            description = hms_data_dictionary.hms[full_code.toLowerCase().replaceAll("_", "")];
        }
        else if (hms_data_dictionary.error[full_code.toLowerCase().replaceAll("_", "")] != undefined) {
            description = hms_data_dictionary.error[full_code.toLowerCase().replaceAll("_", "")];
        }

        let module = ""; let type = "";
        if(flow.get("hms_module_parser") != undefined) {
            let module_parser = flow.get("hms_module_parser");
            module = module_parser(parseInt(hms_code.attr));        
        }
        if (flow.get("hms_level_parser") != undefined) {
            let level_parser = flow.get("hms_level_parser");
            type = level_parser(parseInt(hms_code.code));
        }
        if (type == "") {
            type = "Unknown";
        }

        let d = { "code": "HMS_" + full_code, "url": url, "description": description, "short_code": full_code.replaceAll("_", ""),
            "module": module, "type": type };

        hms_data["HMS_"+ type].push(d);
        count = count + 1;
        //template.push(d);
    }
   // msg.payload.print.hms = hms_data;
    delete msg.payload.print.hms;
    msg.payload.print["HMS_Unknown"] = hms_data["HMS_Unknown"];
    msg.payload.print["HMS_Info"] = hms_data["HMS_Info"];
    msg.payload.print["HMS_Common"] = hms_data["HMS_Common"];
    msg.payload.print["HMS_Serious"] = hms_data["HMS_Serious"];
    msg.payload.print["HMS_Fatal"] = hms_data["HMS_Fatal"];
    msg.payload.print["HMS_Notifications"] = {
        "Unknown": hms_data["HMS_Unknown"].length,
        "Info": hms_data["HMS_Info"].length,
        "Common": hms_data["HMS_Common"].length,
        "Serious": hms_data["HMS_Serious"].length,
        "Fatal": hms_data["HMS_Fatal"].length,
        "Total": count
    };
   //msg.payload.print.hms = template;
}
node.send(msg);

var spd = {}
if(msg.payload.print.spd_mag != undefined) {
    spd.modifier = msg.payload.print.spd_mag;
    msg.payload.print.speed_percent = spd.modifier;
    if (msg.payload.print.spd_lvl == undefined) {
        switch (parseInt(spd.modifier)) {
            case 50:
                spd.profile = "Silent";
                break;
            case 100:
                spd.profile = "Standard";
                break;
            case 124:
                spd.profile = "Sport";
                break;
            case 166:
                spd.profile = "Ludicrous";
                break;
            default:
                spd.profile = "Custom";
                break;
        }
    }
    // need to see if both spd attributes come in always with each other in optimized mode / A1 / P1 series
}
if (msg.payload.print.spd_lvl !== undefined) {
    spd.level = msg.payload.print.spd_lvl;
    switch (msg.payload.print.spd_lvl) {
        case 1:
            spd.profile = "Silent";
            break;
        case 2:
            spd.profile = "Standard";
            break;
        case 3:
            spd.profile = "Sport";
            break;
        case 4:
            spd.profile = "Ludicrous";
            break;
        default:
            spd.profile = "Undefined";
            break;
    }
    if (msg.payload.print.spd != undefined) {
        delete msg.payload.print.spd;
    }

    if (spd.modifier != undefined && parseInt(spd.modifier) != 50
        && parseInt(spd.modifier) != 100 && parseInt(spd.modifier) != 124 && parseInt(spd.modifier) != 166) {
        spd.profile = "Custom";
    }

    msg.payload.print.speed = spd;

}

node.send(msg);

if(msg.payload.print != undefined) {
    msg.payload.printer = msg.payload.print;
    delete msg.payload.print;
}
if (msg.payload.printer != undefined) {
    if(msg.payload.printer.ams != undefined) {
        msg.payload.ams = msg.payload.printer.ams;
        delete msg.payload.printer.ams;

        if(msg.payload.ams.ams != undefined) {
            flow.set("ams_count", msg.payload.ams.ams.length);
        }
    }
    if(msg.payload.printer.s_obj != undefined) {
        msg.payload.printer['skipped_objects'] = msg.payload.printer.s_obj.length;
        delete msg.payload.printer.s_obj;
    }
    if (msg.payload.printer.lights_report != undefined) {
        delete msg.payload.printer.lights_report;
    }
    if (msg.payload.printer.spd_lvl != undefined) {
        delete msg.payload.printer.spd_lvl;
    }
    if (msg.payload.printer.spd_mag != undefined) {
        delete msg.payload.printer.spd_mag;
    }
    if (msg.payload.printer.xcam != undefined) {
        if (msg.payload.printer.xcam['first_layer_inspector'] != undefined) {
            msg.payload.printer['first_layer_inspection'] = msg.payload.printer.xcam['first_layer_inspector'] ? "ON" : "OFF";
        }
        if (msg.payload.printer.xcam['buildplate_marker_detector'] != undefined) {
            msg.payload.printer['buildplate_marker_detection'] = msg.payload.printer.xcam['buildplate_marker_detector'] ? "ON" : "OFF";
        }
    }
    if (msg.payload.printer.xcam_status != undefined) {
        if (msg.payload.printer.xcam == undefined) {
            msg.payload.printer.xcam = {};
        }
        msg.payload.printer.xcam.status = msg.payload.printer.xcam_status;
        delete msg.payload.printer.xcam_status;
    }
    if (msg.payload.printer.flag2 != undefined) {
        let f = parseInt(msg.payload.printer.flag2);
        let f2func = flow.get("flag2Parser");

        msg.payload.printer['firmware_r'] = f2func(f, "is_FW_R");
        msg.payload.printer['firmware_unlocked'] = f2func(f, "is_FW_unlocked");
        msg.payload.printer['ssh_enabled'] = f2func(f, "is_SSH_started");
        delete msg.payload.printer.flag2;/////////////////////////////////////////////////////////////////
    }
    if(msg.payload.printer.home_flag != undefined) {
        msg.payload.printer["is_220_voltage"] = (((msg.payload.printer.home_flag >> 3) & 0x1) != 0);
        flow.set("is_220_voltage", msg.payload.printer["is_220_voltage"]);

        msg.payload.printer['network_connection'] = (((msg.payload.printer.home_flag >> 18) & 0x1) != 0 ? "Wired" : "Wireless");

        let supports_tangle_detect = ((msg.payload.printer.home_flag >> 19) & 0x01) != 0;
        let filament_tangle_detection = (supports_tangle_detect && ((msg.payload.printer.home_flag >> 20) & 0x1) != 0);
        msg.payload.printer['filament_tangle_detection'] = filament_tangle_detection ? "ON" : "OFF";
        if (!supports_tangle_detect) {
            msg.payload.printer['filament_tangle_detection'] = "unsupported";
        }

        let ams_track_remaining_enabled = (((msg.payload.printer.home_flag >> 7) & 0x1) != 0);
        msg.payload.printer['ams_track_filament_enabled'] = ams_track_remaining_enabled;
        let ams_autoswitch_filament = (((msg.payload.printer.home_flag >> 10) & 0x1) != 0);
        if (flow.get("ams_count") > 0) {
            msg.payload.printer['ams_autoswitch_filament_enabled'] = ams_autoswitch_filament ? "ON" : "OFF";//////////
        }
        else {
            msg.payload.printer['ams_autoswitch_filament_enabled'] = "unsupported";
        }

        let nozzle_blob_detect = (((msg.payload.printer.home_flag >> 25) & 0x1) != 0);
        let nozzle_blob_enabled = (((msg.payload.printer.home_flag >> 24) & 0x1) != 0);
        let air_print_detect = (((msg.payload.printer.home_flag >> 29) & 0x1) != 0);
        let air_print_enabled = (((msg.payload.printer.home_flag >> 28) & 0x1) != 0);


        let prompt_sound_enabled = (((msg.payload.printer.home_flag >> 17) & 0x1) != 0);
        // TODO

        let support_user_preset = (((msg.payload.printer.home_flag >> 22) & 0x1) != 0);
        msg.payload.printer['user_presets_supported'] = support_user_preset;

        let auto_recovery_enabled = (((msg.payload.printer.home_flag >> 4) & 0x1) != 0);
        msg.payload.printer["auto_recovery"] = auto_recovery_enabled ? "ON" : "OFF";


        if (nozzle_blob_detect) {
            flow.set("nozzle_blob_detection", true);
            msg.payload.printer["nozzle_blob_detection"] = nozzle_blob_enabled ? "ON" : "OFF";
        }
        else {
            flow.set("nozzle_blob_detection", false);
            msg.payload.printer["nozzle_blob_detection"] = "unsupported";
        }

        if (air_print_detect) {
            flow.set("air_print_detection", true);
            msg.payload.printer["air_print_detection"] = air_print_enabled ? "ON" : "OFF";
        }
        else {
            flow.set("air_print_detection", false);
            msg.payload.printer["air_print_detection"] = "unsupported";
        }

        msg.payload.printer['Homed'] = "None";
        msg.payload.printer["door"] = ((msg.payload.printer.home_flag >> 23) & 0x1) == 1 ? "open" : "closed";
        msg.payload.printer["supports_motor_noise_calibration"] = ((msg.payload.printer.home_flag >> 21) & 0x1) == 1;
        if (msg.payload.printer.home_flag < 0) {
            msg.payload.printer['Homed'] = "XYZ";
        }
        else {
            let x = "";
            if ((msg.payload.printer.home_flag & 1) == 1) {
                x += "X";
            }
            if ((msg.payload.printer.home_flag >> 1 & 1) == 1) {
                x += "Y";
            }
            if ((msg.payload.printer.home_flag >> 2 & 1) == 1) {
                x += "Z";
            }
            if (x == "") {
                x = "None";
            }
            msg.payload.printer['Homed'] = x;
        }

        if (flow.get("model") == "P1P") {
            let supported_upgrade_kit = (((msg.payload.printer.home_flag >> 27) & 0x1) != 0);
            let has_upgrade_kit = (((msg.payload.printer.home_flag >> 26) & 0x1) != 0);

            msg.payload.printer["supports_upgrade_kit"] = supported_upgrade_kit;

            msg.payload.printer["upgrade_kit"] = has_upgrade_kit ? "Installed" : "None";

        }

        delete msg.payload.printer.home_flag;
    }

    if (msg.payload.printer.ipcam != undefined) {
        if (msg.payload.printer.ipcam.ipcam_dev != undefined) {
            msg.payload.printer.ipcam.ipcam_dev = msg.payload.printer.ipcam.ipcam_dev == "1" ? "Present" : "No Camera";
            flow.set("has_ipcam", (msg.payload.printer.ipcam.ipcam_dev == "Present"));

        }
        if (msg.payload.printer.ipcam.timelapse != undefined) {
            //node.warn(msg.payload.printer.ipcam.timelapse);
            msg.payload.printer.ipcam.timelapse = msg.payload.printer.ipcam.timelapse == "disable" ? "OFF" : "ON";
            msg.payload.printer["timelapse_state"] = msg.payload.printer.ipcam.timelapse;
        }
        if (msg.payload.printer.ipcam.ipcam_record != undefined) {
            msg.payload.printer.ipcam.ipcam_record = msg.payload.printer.ipcam.ipcam_record == "disable" ? "OFF" : "ON";
            msg.payload.printer["recording_state"] = msg.payload.printer.ipcam.ipcam_record;
        }
        if(msg.payload.printer.ipcam.resolution != undefined) {
            msg.payload.printer.camera_resolution = msg.payload.printer.ipcam.resolution;
        }
    }
    msg.payload.printer.ams_count = flow.get("ams_count");
}
