    let main_status = (status & 0xFF00) >> 8;
    switch (main_status) {
        case 0:
        case 0x00:
            return "AMS Idle";
        case 1:
        case 0x01:
            return "AMS Filament Change";
        case 2:
        case 0x02:
            return "AMS RFID Identifying";
        case 3:
        case 0x03:
            return "AMS Assist";
        case 4:
        case 0x04:
            return "AMS Calibration";
        case 16:
        case 0x10:
            return "AMS Self-Check";
        case 32:
        case 0x20:
            return "AMS Debug";
        case 255:
        case 0xFF:
        default:
            return "Unknown";
    }
}
