function getHMSLevel(code) {
    let uint_id = (code >> 16);
    switch (uint_id) {
        case 1:
            return "Fatal";
        case 2:
            return "Serious";
        case 3:
            return "Common";
        case 4:
            return "Info";
        default:
            return "Unknown";
    }
}

function getHMSModule(attr) {
    let uint_id = ((attr >> 24) & 0xFF);
    switch (uint_id) {
        case 0x05:
            return "MainBoard";
        case 0x0C:
            return "XCam";
        case 0x07:
            return "AMS";
        case 0x08:
            return "Toolhead";
        case 0x03:
            return "MC";
        default:
            return "Unknown - "+ uint_id.toString();
    }
}

function toHex(value, min_length) {
    let hex = value.toString(16);
    while (hex.length < min_length) {
        hex = "0"+ hex;
    }
    return hex;
}

function getHMSLongCode(attr, code) {
    var module_id = (attr >> 24) & 0xFF;
    var module_num = (attr >> 16) & 0xFF;
    var part_id = (attr >> 8) & 0xFF;
    var reserved = (attr >> 0) & 0xFF;

    var level = code>>16;
    if (level > 4 || level < 0) {
        level = 0;
    }

    var msg_code = code & 0xFFFF;

    var longcode = toHex(module_id,2) + ""+toHex(module_num,2) +""+toHex(part_id,2)+"00000"+ toHex(parseInt(level),0) +""+ toHex(msg_code, 4);
    return longcode;
}

flow.set("hms_longcode_parser", getHMSLongCode);
flow.set("hms_level_parser", getHMSLevel);
flow.set("hms_module_parser", getHMSModule);

