function sendNonEmpty(message) {
    if (message.topic.includes("/uptime")) {
        node.send(message);
        return;
    }
    if(message.payload == 0 || (message.payload != undefined && (message.payload != "" || 
        (Object.keys(message.payload) != undefined && Object.keys(message.payload).length != 0)))) {
        node.send(message);
    }
}

