let root_topic = flow.get("root_topic");
let ha_device_name = flow.get("HA_DEVICE");
if (msg.topic == "machine_serial" || msg.topic == "print_preview"
     || msg.topic == "msg" || msg.topic == "active_print_time"
     || msg.topic == "filament_printed") {
    return;
}
if(msg.topic == "print_status") {
    if(msg.payload != "RUNNING" && !msg.payload.includes("PAUSE")) {
        flow.set("print_start_epoch", 0);        
    }
}
if (msg.topic == "reset_filter" || msg.topic == "resume_print" || msg.topic == "pause_print"
    || msg.topic == "clear_external_spool" || msg.topic == "force_ftp_fetch"
    || msg.topic.includes("logo_light") || msg.topic.includes("nozzle_light")) {
    return;
}
if(msg.topic == "nozzle_diameter") {
    flow.set("nozzle_diameter", msg.payload);
}
if(msg.topic.startsWith("set_") && (msg.topic.endsWith("_temp") || msg.topic.endsWith("_speed_percent"))) {
    return;
}
else if (msg.topic == "last_calibrated_pa" && msg.payload == "") {
    return;
}
else if (msg.topic == "toggle_timelapse" || msg.topic == "toggle_recording" || msg.topic == "gcode_command" || 
         (msg.topic.startsWith("toggle_") && (msg.topic.includes("air_print") || msg.topic.includes("nozzle_blob") 
            || msg.topic.includes("auto_recovery") || msg.topic.includes("filament_tangle") || msg.topic.includes("auto_switch") 
            || msg.topic.includes("prompt_sound") || msg.topic.includes("buildplate") || msg.topic.includes("layer_inspect")))) {
    return;
}
else if (msg.topic == "hw_switch_state") {
    msg.topic = "extruder_loaded";
    if(msg.payload == "1") {
        msg.payload = true;
    }
    else {
        msg.payload = false;
    }
}
else if (msg.topic == "uptime") {
    let oldTopic = msg.topic;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    hasAttr = true;
    let oldPayload = msg.payload;
    msg.payload = {"Note:": "Uptime since NodeRed connected to printer. Resets on redeploy. For a closer machine uptime, check _status sensor is \"online\"."};

    node.send(msg);
    msg.payload = oldPayload;
    msg.topic = oldTopic;
}
else if (msg.topic == "machine_name") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    let oldPayload = msg.payload;
    msg.payload = { "Serial Number": flow.get("printer_serial")};
    node.send(msg);
    msg.payload = oldPayload;
    msg.topic = oldTopic;
}
else if (msg.topic == "wifi_signal")
    msg.payload = msg.payload.match(/^-?\d+/)[0];
/*else if (msg.topic == "door") {
    entity_type = "event";
    msg.payload = {"event_type": msg.payload}
}*/
else if (msg.topic == "xcam") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = msg.payload.status;
}
else if (msg.topic == "upload") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = msg.payload.message;
}
else if (msg.topic == "camera_rtsp") {
    var oldTopic = msg.topic;
    var oldPayload = msg.payload;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    if (oldPayload != "Disabled") {
        msg.payload = {"URL": oldPayload, "User": "bblp", "Password": "<access_code>", "Protocol": "TCP", "SSL Verify": "disable", "Framerate": 30, "Port (Optional)": 322, "LAN Mode": "Not Required"};
        node.send(msg);
        oldPayload = "Enabled";
    }
    msg.topic = oldTopic;
    msg.payload = oldPayload;
}
else if (msg.topic == "upgrade_state") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    let state = "AVAILABLE";
    if (msg.payload.new_version_state == 1) {
        msg.payload = state;
    }
    else {
        msg.payload = msg.payload.status;
    }
}
else if (msg.topic == "stage") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";

    var current = msg.payload.current;
    var prev = {};
    var count = 0;

    if(msg.payload.prev != undefined) {
        for (var stg of msg.payload.prev) {
            prev[count.toString()] = stg;
            count += 1;
        }
            msg.payload = prev;
            node.send(msg);
    }

    msg.topic = oldTopic;
    msg.payload = current;
}
else if (msg.topic == "ipcam") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = msg.payload.ipcam_dev;
}
else if (msg.topic == "vt_tray") {
    var oldTopic = msg.topic;
    if (msg.payload.color != undefined
        && !msg.payload.color.startsWith("#")) {
        msg.payload.color = "#" + msg.payload.color;
    }
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = msg.payload.type;
}
else if (msg.topic == "subtask") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = msg.payload.name;
}
else if (msg.topic == "print") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = msg.payload.type;
    if(msg.payload == undefined || msg.payload == "") {
        msg.payload = "None";
    }
}
else if (msg.topic.includes("nodered")) {
    var oldTopic = msg.topic;
    let old_payload = msg.payload;
    hasAttr = true;
    let url = "https://www.wolfwithsword.com/bambulab-to-home-assistant-nodered-configurator/";
    if (msg.topic.includes("advanced")) {
        url = "https://www.wolfwithsword.com/bambulab-advanced-setup-with-homeassistant-grafana-and-postgres/";
    }
    msg.payload = {
                    "URL": url,
                    "Release": "https://github.com/WolfwithSword/Bambu-HomeAssistant-Flows/releases",
                    "All Guides": "https://www.wolfwithsword.com/tag/bambu-lab-guides/"
                }
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = old_payload;

}
else if (msg.topic == "bed_temperature") {
    var oldTopic = msg.topic;
    hasAttr = true;
    if (msg.payload.target_temperature != undefined) {
        msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
        node.send(msg);
    }
    if(msg.payload.temperature == undefined) {
        return;
    }
    msg.topic = oldTopic;
    msg.payload = msg.payload.temperature;
}
else if (msg.topic == "nozzle_temperature") {
    var oldTopic = msg.topic;
    hasAttr = true;
    if(msg.payload.target_temperature != undefined) {
        msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
        node.send(msg);
    }
    if (msg.payload.temperature == undefined) {
        return;
    }
    msg.topic = oldTopic;
    msg.payload = msg.payload.temperature;
}
else if (msg.topic == "chamber_temperature") {
    msg.payload = msg.payload.temperature;
}
else if (msg.topic == "plate_name" && msg.payload != "") {
    let match = msg.payload.match(/_?(plate_[1-9][0-9]?[0-9]?)/);
    if (match != undefined && match.length > 1) {
        msg.payload = match[1];
    }
}
else if (msg.topic == "gcode") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    if(msg.payload.file == undefined) {
        return;
    }
    msg.payload = msg.payload.file;
}
else if (msg.topic.includes("HMS")) {
    //msg.topic = "HMS";
    var oldTopic = msg.topic;
    var numOfCodes = 0;
    hasAttr = true;
    if (oldTopic != "HMS_Notifications"){
        msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
        numOfCodes = msg.payload == undefined || msg.payload.length == 0 ? 0 : msg.payload.length;
        var links = {};
        for (var data of msg.payload) {
            //if (data.type != undefined && data.type != "") {
            //    links[data.code.replaceAll(" ", "_") + " Severity"] = data.type;
            //}
            if ( data.description != undefined && data.description != "") {
                links[data.code.replaceAll(" ", "_")+" Description"] = data.description;
            }
            if(data.module != undefined && data.module != "") {
                links[data.code.replaceAll(" ", "_") + " Module"] = data.module;
            }

            links[data.code.replaceAll(" ", "_")] = data.url;
        }
        msg.payload = links;
        if(msg.payload != undefined && msg.payload != "" && Object.keys(msg.payload).length != 0) {
            node.send(msg);
        }
        msg.topic = oldTopic;
        msg.payload = numOfCodes;
    }
    else {
        numOfCodes = msg.payload["Total"];
        delete msg.payload["Total"];
        msg.topic = "HMS";
        if (msg.payload != undefined && msg.payload != "" && Object.keys(msg.payload).length != 0) {
            msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
            node.send(msg);
        }
        msg.topic = "HMS";
        msg.payload = numOfCodes;
    }
}
else if (msg.topic == "speed") {
    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/select/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + "speed_info" + "/attr";
    node.send(msg);
    msg.topic = "speed";
    msg.payload = msg.payload.profile;
    msg.topic = root_topic + "/select/" + ha_device_name + "/" + "speed" + "/state";
    node.send(msg);
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + "speed_info" + "/state";
    node.send(msg);
    return;
}
else if (msg.topic == "camera_resolution") {
    if (!flow.get("has_ipcam")) {
        return;
    }
    msg.topic = root_topic + "/select/" + ha_device_name + "/" + msg.topic + "/state";
    node.send(msg);
    return;
}
else if (msg.topic == "chamber_light") {
    msg.payload = msg.payload.toUpperCase();
    msg.topic = root_topic + "/light/" + ha_device_name + "/" + msg.topic + "/state";
    node.send(msg);
    return;
}
else if (msg.topic.match(/(.*)fan(.*)/) && msg.topic !== "fan_gear" && msg.topic != "heatbreak_fan"
    && msg.topic != "aux_part_fan") {
    let orig_topic = msg.topic;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + orig_topic + "/state";
    node.send(msg);
    msg.topic = root_topic + "/fan/" + ha_device_name + "/" + orig_topic + "/percent/state";
    msg.payload = parseInt(msg.payload) / 10;
    node.send(msg);
    if(msg.payload == 0 || msg.payload == "0") {
        msg.payload = "OFF";
        msg.topic = root_topic + "/fan/" + ha_device_name + "/" + orig_topic+ "/state";
        node.send(msg);
    }
    else {
        msg.payload = "ON";
        msg.topic = root_topic + "/fan/" + ha_device_name + "/" + orig_topic + "/state";
        node.send(msg);
    }
    return;
}

msg.topic = root_topic + "/" + entity_type + "/" + ha_device_name + "/" + msg.topic + "/state";

if(hasAttr || msg.topic.includes("/uptime")) {
    sendNonEmpty(msg);
}
else {
    node.send(msg);
}

let can_request = flow.get("can_request");

if (msg.status_change != undefined) {
    if(msg.status_change == "no_data")
        node.status({ fill: "red", shape: "ring", text: "No Data" });
    else if (msg.status_change == "warn_no_data")
        node.status({ fill: "yellow", shape: "ring", text: "No Recent Data" });
    return;
}
if (msg.payload == undefined || 
    (Object.keys(msg.payload) != undefined
     && Object.keys(msg.payload).length == 0)) { return };
let machine_name = flow.get("printer_name");
let machine_serial = flow.get("serial_number");
let manufacturer = "Bambu Labs";
let model = flow.get("model");

let camera_stream_url = "{STREAM_URL}";
//let camera_rtsp = "{RTSP}"
let snapshot_url = "{SNAPSHOT_URL}";

if(msg.payload.original_host != undefined){
    flow.set("printer_real_ip", msg.payload.original_host);
    delete msg.payload.original_host;
}

if(msg.payload.status != undefined) {
    if (msg.payload.status == "offline") {
        msg.payload.print["uptime"] = 0;
        flow.set("online_time", -1);
        node.send([null, msg]);
        return;
    }
    delete msg.payload.status;
}

if(msg.payload.mc_print != undefined) {
    msg.payload.mc_print.machine_name = machine_name;
    msg.payload.mc_print.machine_serial = machine_serial;
    msg.payload.mc_print.manufacturer = manufacturer;
    let param = msg.payload.mc_print.param;
    if (typeof param === 'string' || param instanceof String) { 
        if(param.includes("[AMS]")) {
            msg.payload.mc_print.model = "AMS";
            node.send([msg, null]);
        }
        else {
            msg.payload.mc_print.model = model;
            node.send([msg, null]);
        }
    }        
    else {
        msg.payload.mc_print.model = model;
        node.send([msg, null]);
    }
    node.status({ fill: "green", shape: "ring", text: "Data Received" });
}

if (msg.payload.print != undefined) {
    if (msg.payload.print.command != undefined && msg.payload.print.command == "project_file") {
        if (flow.get("printjob_mapping") == undefined) {
            flow.set("printjob_mapping", {});
        }
        let mapping = flow.get("printjob_mapping");
        let d = msg.payload.print;
        if (d['subtask_id'] != undefined && d['subtask_name'] != undefined && d['url'] != undefined) {
            if (!d['url'].startsWith("file:///data/model/history")) {
                let is_update = false;
                if (mapping[d['subtask_id']] != undefined) {
                    is_update = true;
                }
                mapping[d['subtask_id']] = {
                    "subtask_name": d["subtask_name"],
                    "url": d['url'],
                    "timestamp": new Date()
                };
                flow.set("printjob_mapping", mapping);

                // delete unique jobs older than 3 days, but only trigger once we get a new one to add
                if (!is_update) {
                    let keys = Object.keys(mapping);
                    let cutoff = new Date();
                    cutoff.setDate(cutoff.getDate() - 7); //7 days
                    for (var k of keys) {
                        if (cutoff.getTime() > (new Date(mapping[k]['timestamp'])).getTime()) {
                            delete mapping[k];
                        }
                    }
                    flow.set("printjob_mapping", mapping);
                }
            }

        }
    }

    if (msg.payload.print.command != undefined && msg.payload.print.command == "project_file" && msg.payload.print.timestamp != undefined) {
        let start = msg.payload.print.timestamp.toString();
        msg.payload.print = {
            "gcode_start_time": start,
            "command": "manual"
        }
    }
    else if (msg.payload.print.command != undefined && msg.payload.print.command == "project_file" && msg.payload.print.timestamp == undefined) {
       let start = Math.round(Date.now() / 1000).toString();
        msg.payload.print = {
            "gcode_start_time": start,
            "command": "manual"
        }
    }

    msg.payload.print.machine_name = machine_name;
    msg.payload.print.machine_serial = machine_serial;
    msg.machine_serial = machine_serial;
    msg.payload.print.manufacturer = manufacturer;
    msg.payload.print.model = model;

    if (msg.payload.print.ams !== undefined && msg.payload.print.ams.ams != undefined) {
        for (var ams of msg.payload.print.ams.ams) {
            ams.printer_name = machine_name
            ams.printer_serial = machine_serial
        }
        if (Object.keys(msg.payload.print).length > 50) {
        }
    }
    msg.payload.print.status = "online";
    if (flow.get("online_time") == -1 || flow.get("online_time") == undefined) {
        flow.set("online_time", new Date());
        flow.set("last_time")
        msg.payload.print["uptime"] = 0;
    }

    let now = new Date()
    if (flow.get("online_time") != undefined && flow.get("online_time") != -1){ //&& Math.abs(flow.get("online_time").getTime() - now.getTime())/1000 >= 1*60) {
        // whenever we get a msg, update uptime
        msg.payload.print["uptime"] = Math.abs(flow.get("online_time").getTime() - now.getTime()) / 1000;
    }

    msg.payload.print.reset_filter = "";

    if (can_request) {
        msg.payload.print.pause_print = "";
        msg.payload.print.resume_print = "";
        msg.payload.print.stop_print = "";

        msg.payload.print.force_update = "";
        if (flow.get("allow_gcode_control") == true) {
            msg.payload.print.gcode_command = "";
            msg.payload.print.send_gcode = "";
        }
    }

    if (flow.get("basic_is_latest_flow") != undefined) {
        if (flow.get("basic_is_latest_flow")) {
            msg.payload.print.nodered_flow_status = "Up to Date";
        }
        else {
            msg.payload.print.nodered_flow_status = "Update Available";
        }
    }
    if(flow.get("basic_flow_version") != undefined) {
        msg.payload.print.nodered_flow_version = flow.get("basic_flow_version");
    }

    if (flow.get("has_adv_flow")) {
        if(flow.get("advanced_flow_version") != undefined) {
            msg.payload.print.nodered_advanced_flow_version = flow.get("advanced_flow_version");
        }

        if (flow.get("advanced_is_latest_flow") != undefined) {
            if (flow.get("advanced_is_latest_flow")) {
                msg.payload.print.nodered_advanced_flow_status = "Up to Date";
            }
            else {
                msg.payload.print.nodered_advanced_flow_status = "Update Available";
            }
        }
    }

    if(!model.startsWith("A1")) {
        if (can_request) {
            msg.payload.print.logo_light_on = "";
            msg.payload.print.logo_light_off = "";
            msg.payload.print.nozzle_light_on = "";
            msg.payload.print.nozzle_light_off = "";
        }
    }
    if (flow.get("has_db_flow") != undefined && flow.get("has_db_flow") == true) {
        msg.payload.print.active_print_time = "";
        msg.payload.print.filament_printed = "";
    }

    if (can_request) 
        msg.payload.print.home = "";

    msg.payload.print.mqtt_connection_type = flow.get("P1P_CLOUD_MODE") ? "Bambu Cloud" : "Local";
    if (flow.get("has_ipcam") && can_request) {
        msg.payload.print.toggle_recording = "";
        //msg.payload.print.toggle_timelapse = "";////////////////////////
    }

    if (can_request) {
        if(flow.get("ams_count") > 0) {
            msg.payload.print.toggle_auto_switch = "";
        }
        if (flow.get("model").startsWith("X1")) {
            msg.payload.print.toggle_first_layer_inspection = "";
            msg.payload.print.toggle_buildplate_detection = "";
        }
        msg.payload.print.set_bed_temp = "";
        msg.payload.print.set_nozzle_temp = "";
        msg.payload.print.set_speed_percent = "";
        msg.payload.print.toggle_auto_recovery = "";
        let can_heat_chamber = (flow.get("model") == "X1E"); // change if retroactive
        if(can_heat_chamber) {
            msg.payload.print.set_chamber_temp = "";
        }
    }

    //msg.payload.print.print_preview = "";

    if (flow.get("has_adv_flow")) {
        //msg.payload.print.plate_type = "";
        msg.payload.print.print_preview = "";
        msg.payload.print.force_ftp_fetch = "";
    }

    if (can_request){
        msg.payload.print.clear_external_spool = "";
    }
    if(model == "X"+"1C" || model == "X"+"1") {
        let versions = flow.get("mc_print_versions");
        if (versions[model] != undefined) {
            let valid_versions = versions[model];
            if (valid_versions.length > 0 && valid_versions.includes(flow.get("fw_version"))) {
                msg.payload.print.last_calibrated_pa = "";
            }
        }

      /*  if (msg.payload.printer != undefined && msg.payload.printer.SW_version != undefined && msg.payload.printer.SW_version != ""
        && flow.get("x1plus_installed") != undefined && flow.get("x1plus_installed") == true) {
            if(msg.payload.printer.SW_version.startsWith("99")) {
                if(flow.get("x1plus_active") == undefined || flow.get("x1plus_active") == false) {
                    node.warn("[INFO] X1Plus CFW is currently Active");
                }
                flow.set("x1plus_active", true); 
            }
            else{
                if(flow.get("x1plus_active") == undefined || flow.get("x1plus_active") == true){
                    node.warn("[INFO] X1Plus CFW is currently Not Active");
                }
                flow.set("x1plus_active", false); 
            }
        }*/
        if (flow.get("x1plus_installed") != undefined && flow.get("x1plus_installed") == true) {
            if (flow.get("x1plus_active") != undefined) {
                if (flow.get("x1plus_active")) 
                    msg.payload.print.x1plus = "Active";
                else
                    msg.payload.print.x1plus = "Inactive";
            }
        }
    }

    if (can_request)
        msg.payload.print.unload_filament = "";

    if(snapshot_url != undefined && snapshot_url != "" && snapshot_url != "{" + "SNAPSHOT_URL" + "}") {
        msg.payload.print.snapshot_url = snapshot_url;
    }

    if (camera_stream_url != undefined && camera_stream_url != "" && camera_stream_url != "{" + "STREAM_URL" + "}") {
        msg.payload.print.camera_stream_url = camera_stream_url;
    }

    if(msg.payload.print.ipcam != undefined && msg.payload.print.ipcam.rtsp_url != undefined) {
        if(msg.payload.print.ipcam.rtsp_url == "disable") {
            msg.payload.print.camera_rtsp = "Disabled";
        }
        else if (msg.payload.print.ipcam.rtsp_url.startsWith("rtsp")) {
            msg.payload.print.camera_rtsp = msg.payload.print.ipcam.rtsp_url;
            if(msg.payload.print.ipcam.ipcam_dev == undefined) {
                delete msg.payload.print.ipcam;
            }
        }
        // rtsps://bblp:[access_code]@[lan_ip]:322/streaming/live/1    TCP
    }

    if(msg.payload.print.cali_version != undefined && msg.payload.print.cali_version >= 0 && model.includes("X1")) {
        flow.set("supports_calibration", true);
    }

    node.status({ fill: "green", shape: "ring", text: "Data Received" });

    if (flow.get("wws_bambu_" + flow.get("printer_serial")) != undefined) {
        let d = flow.get("wws_bambu_" + flow.get("printer_serial"));
        msg.payload.print["IP_Address"] = d["IP Address"];
        msg.payload.print["Device_Dev_Name"] = d["Name"];
        msg.payload.print["Connection_Type"] = d["Connection Type"];
        msg.payload.print["Series"] = d["Series"];
        msg.payload.print["SSDP_Model"] = d["Model"];
