function parseModel(model) {
    if (model == "3DPrinter-X1") {
        return "BL-P002";
    }
    else if (model == "3DPrinter-X1-Carbon") {
        return "BL-P001";
    }
    else if (model == "BL-P001") {
        return model;
    }
    else if (model == "BL-P003") {
        return model;
    }
    return "";
}

function parseTrueModel(parsed_model) {
    switch (parsed_model) {
        case "C11":
            return "P1P";
        case "C12":
            return "P1S";
        case "BL-P002":
            return "X1";
        case "BL-P001":
            return "X1C";
        case "C13": 
            return "X1E";
        case "N1":
            return "A1 Mini";
        case "N2S":
            return "A1";
        default: 
            return "Unknown - " + parseModel;
    }
}

function parseSeries(true_model) {
    if (true_model == "P1P" || true_model == "P1S") {
        return "P1 Series";
    }
    else if (true_model == "X1C" || true_model == "X1" || true_model == "X1E") {
        return "X1 Series";
    }
    else if (true_model == "A1 Mini" || true_model == "A1"){
        return "A1 Series";
    }
    return "";
}

msg.payload = msg.payload.split("\n");
msg.payload.shift();
let data = {};
for (var x of msg.payload) {
    let entry = x.split(/:(.*)/s);
    if(entry.length > 1){
        let key = entry[0].trim();
        let value = entry[1].trim();
        switch(key) {
            case "Location":
                data["IP Address"] = value;
                break;
            case "USN":
                data["Serial Number"] = value;
                break;
            case "DevModel.bambu.com":
                data["Technical Model"] = parseModel(value);
                data["Model"] = parseTrueModel(data['Technical Model']);
                data["Series"] = parseSeries(data['Model']);
                break;
            case "DevName.bambu.com":
                data["Name"] = value;
                break;
            case "DevConnect.bambu.com":
                data["Connection Type"] = value;
        }

    }
}
data["Last Updated"] = Date.now();
msg.payload = data;
node.send(msg);

let name = "wws_bambu_" + msg.payload["Serial Number"];
